SELECT u.nick_name, u.avatar, u.web_site, c.user_id, c.comment_id, c.comment_content, c.create_time FROM blog_comment c JOIN sys_user u ON c.user_id = u.user_id
topic_id = #{commentVo.topicId}
AND type = #{commentVo.type} AND c.state = 1 AND parent_id IS NULL
ORDER BY c.comment_id DESC LIMIT #{current},#{size}

SELECT * FROM ( SELECT c.user_id, u.nick_name, u.avatar, u.web_site, c.reply_user_id, r.nick_name AS reply_nickname, r.web_site AS reply_web_site, c.comment_id, c.parent_id, c.comment_content, c.create_time, row_number () over ( PARTITION BY parent_id ORDER BY create_time ASC ) row_num FROM blog_comment c JOIN sys_user u ON c.user_id = u.user_id JOIN sys_user r ON c.reply_user_id = r.user_id WHERE c.state = 1 AND parent_id IN (
#{commentId}
) ) t WHERE 4 > row_num

SELECT parent_id AS comment_id, count(*) AS reply_count FROM blog_comment WHERE state = 1 AND parent_id IN
#{commentId}
GROUP BY parent_id

SELECT topic_id AS comment_id, COUNT(*) AS comment_count FROM blog_comment WHERE topic_id IN
#{topicId}
AND parent_id IS NULL GROUP BY topic_id
